
public class OperatorExam {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 7;
		int b = 3;
		
		int c = a+b;
		int d = a-b;
		int e = a*b;
		int f = a%b;
		
		System.out.printf("C는 : %d\n",c);
		System.out.printf("D는 : %d\n",d);
		System.out.printf("E는 : %d\n",e);
		System.out.printf("F는 : %d\n",f);
		
	}

}
